import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';

@Component({
  selector: 'app-board',
  templateUrl: './board.component.html',
  styleUrls: ['./board.component.css']
})
export class BoardComponent implements OnInit {

  array: number[][];
  arrayGame: number[][];
  maxValue: number = 0;
  sizeArray: number = 3;
  player: number = 1;
  @Input() winner: number;
  @Output() gameChangeBoard = new EventEmitter<any>();

  constructor() { }

  ngOnInit(): void {
    this.configInit();
  }
  public configInit(){
    this.player = 1;
    this.array = undefined;
    this.winner = undefined;
    this.arrayGame = undefined;
    this.array = this.createArray(this.sizeArray);
    this.arrayGame = this.createArray(this.sizeArray);
  }

  public createArray(size: number):  number[][] {
    var i = 0;
    var matrix: number[][] = [];
    while (i < size) {
      let j = 0;
      while (j < size) {
        if (!matrix[i]) {
          matrix[i] = [];
        }
        matrix[i][j] = 0;
        j = j + 1;
      }
      i = i + 1;
    }
    return matrix;
  }

  public nextPlayer(result) {
    this.arrayGame[result.i][result.j] = result.value;
    this.gameChangeBoard.emit(
      {
        'board':this.arrayGame,
        'nowPlayer': this.player
      }
      );
    this.player = this.player == 1 ? 2 : 1;
  }
}
<h1 class='text-center'>Game TicTacToe</h1>
<h2 class='text-center'>{{winner?'Winner':'Player turn'}} : {{player==1?'X':'O'}}</h2>

<div class="row " style='margin-bottom: 20px;'>
  <div class="col-md-4 text-center">
    <button class="btn btn-primary" (click)='sizeArray=3;configInit();'>EASY</button>
  </div>
  <div class="col-md-4 text-center">
    <button class="btn btn-primary" (click)='sizeArray=4;configInit();'>MEDIUM</button>
  </div>
  <div class="col-md-4 text-center">
    <button class="btn btn-primary" (click)='sizeArray=5;configInit();'>HARD</button>
  </div>
</div>
  <ng-container  *ngIf="array">
    <ng-container *ngFor="let row of array; let i = index">
      <div style="display: table">
        <ng-container *ngFor="let col of row; let j = index">
          <div class="text-center color-{{ col }} elem-game ">
            <app-cell [value]='player' [i]='i' [j]='j' [win]='winner' (changeStateEmitter)='nextPlayer($event)'></app-cell>
          </div>
        </ng-container>

      </div>
    </ng-container>
    <div class="row">
      <div class="col-md-12 text-center">
        <button class="btn btn-success" (click)="configInit()">
          reload
        </button>
      </div>
    </div>
  </ng-container>
