import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-image-object',
  templateUrl: './image-object.component.html',
  styleUrls: ['./image-object.component.less']
})
export class ImageObjectComponent implements OnInit {
  public number = 1;
  constructor() { }

  ngOnInit() {
    this.number = Math.floor(Math.random() * 10);
  }


  public onClick(e) {
    console.log(e);
  }
}
<div class="card" >
    <img [src]="'https://picsum.photos/600/300?random='+number.toString()" >
    <button type="button" class="btn btn-success btn-xs" (click)="onClick($event)">
      <i class="fa fa-check"></i>
      GOOD IMAGE
    </button>
    <button type="button" class="btn btn-warning btn-xs" (click)="onClick($event)">
      <i class="fa fa-question"></i>
      I DON'T KNOW
    </button>
    <button type="button" class="btn btn-danger btn-xs" (click)="onClick($event)">
      <i class="fa fa-times"></i>
      BAD IMAGE
    </button>
  </div>
