import { Component, OnInit, Input } from '@angular/core';
import { skillDto } from '../../dtos/skillDto';

@Component({
  selector: 'skill',
  templateUrl: './skills.component.html',
  styleUrls: ['./skills.component.less']
})
export class SkillsComponent implements OnInit {
  @Input() skill: skillDto;
  constructor() { }


  ngOnInit() {
  }

}
<div class="row">
  <div class="col-md-4">
    <p class="text-right title-skill">
      <a class="text-skill" href="{{skill.link}}">{{skill.name}}</a>
    </p>
  </div>
  <div class="col-md-8">
    <div class="progress">
      <div class="progress-bar bg-blue1 progress-meter"
        role="progressbar"
        [style.width]="skill.value + '%'"
        aria-valuemin= "0"
        aria-valuemax= "100"></div>
    </div>
  </div>
</div>
