import { Component, OnInit } from '@angular/core';
import { ApiPokemonService } from '../api-pokemon.service';
@Component({
  selector: 'app-pokemon-to-discovery',
  templateUrl: './pokemon-to-discovery.component.html',
  styleUrls: ['./pokemon-to-discovery.component.css'],
  providers: [ApiPokemonService]
})
export class PokemonToDiscoveryComponent implements OnInit {
  loading: boolean = false;
  constructor(public apiService: ApiPokemonService) { }

  ngOnInit(): void {
  }

  randomPokemon() {
    this.apiService.showPokemon = true;
    let randomNumber: number = Math.floor(Math.random() * this.apiService.defaultOption);
    setTimeout(() => {
      this.apiService.loading = true;
      this.apiService.getPokemon(randomNumber.toString()).subscribe(
        (data) => {

          this.apiService.showPokemon = false;
          this.apiService.loading = false;
          this.apiService.pokemonSelected = data;

        }
      )
    }, 3000
    )
  }
}
<div class="card border-primary mb-3" *ngIf="apiService.pokemonSelected">
  <div class="card-body">
    <h1 class="text-center" *ngIf="apiService.showPokemon">
      {{ apiService.pokemonSelected.name }}
      <span style="font-size: 20px; color: grey">
        [{{ apiService.pokemonSelected.id }}]
      </span>
    </h1>
    <div class="row">
      <ng-container *ngIf="!apiService.loading">
        <img
          src="{{
            apiService.pokemonSelected.sprites.other['official-artwork']
              .front_default
          }}"
          class="image-principal {{
            apiService.showPokemon ? '' : 'colorizable'
          }}"
        />

        <button class="btn btn-info" (click)="randomPokemon()">
          <i class="fa fa-undo"></i>
        </button>
      </ng-container>

      <ng-container *ngIf="apiService.loading">
        <div class="col text-center">
          <i class="fa fa-spinner fa-10x fa-spin"></i>
        </div>
      </ng-container>
    </div>
  </div>
</div>
